mapper.select:
CREATE TEMPORARY TABLE user_country ( name char(25), counrtyCode char(3) );
INSERT INTO user_country VALUES ('${item.name}', '${item.countryCode}');
SELECT DISTINCT user_country.name AS name, country.Name AS countryName, city.Name AS cityName, city.Population AS cityPopulation FROM world.country AS country INNER JOIN user_country ON user_country.counrtyCode = country.code LEFT JOIN world.city AS city ON city.CountryCode = country.Code ORDER BY country.Name ASC, city.Population DESC;
DROP TEMPORARY TABLE user_country;
