mapper.select:
CREATE TEMPORARY TABLE user_country ( name char(25), counrtyCode char(3) );
INSERT INTO user_country VALUES ('${item.name}', '${item.countryCode}');
SELECT name, countryName, cityName, cityPopulation FROM ( SELECT user_country.name AS name, country.Name AS countryName, city.Name AS cityName, city.Population AS cityPopulation, ROW_NUMBER() OVER ( PARTITION BY country.Name ORDER BY city.Population DESC ) AS rank FROM country AS country INNER JOIN user_country ON user_country.counrtyCode = country.code LEFT JOIN city AS city ON city.CountryCode = country.Code ) AS pop_rank WHERE rank <= 3 ORDER BY rank ASC;
DROP TABLE user_country;
